target.script:
			
			importClass(java.io.File);
			importClass(Packages.org.eclipse.jgit.api.Git);
			importClass(Packages.org.eclipse.jgit.lib.ObjectId);
			
			var git = Git.open(new File("."));
			
			var tagList = git.tagList().call();
			var lastTag = tagList.get(tagList.size()-1);
			// Need to "peel" the tag to access underlying commit:
			lastTag = git.getRepository().peel(lastTag);
			var tagCommitId = lastTag.getPeeledObjectId();
			
			var commitIter = git.log().call().iterator();
			var commitsSince = 0;
			while (commitIter.hasNext()) {
			    var commit = commitIter.next();
				if (commit.getId().equals(tagCommitId)) {
			        break;
			    }
			    commitsSince++;
			}

			project.setProperty("build.version",
			    lastTag.getName().substring("refs/tags/".length) + "." + commitsSince);
